output = function() {
  db.destroy(function destroyDatabase(err, info) {
    if (err) {
      output({error: $.create(err)});
    } else {
      output({info: $.create(info)});
    }
  });
};

on.input.in = function() {
  $.db.get(response.id, function(err, doc) {
    if (err) {
      output({error: $.create(err)});
    } else {
      output({out: $.create(doc)});
    }
  });
};

on.input.in = function() {
  $.db.post($.in, function(err, response) {
    if(err) {
      output({error: $.create(err)});
    } else {
      // get full updated document.
      $.db.get(response.id).then(function(doc) {
        output({out: $.get('in')});
      });
    }
  });
};

on.input.in = function() {
  $.db.put($.in, function(err, response) {
    if(err) {
      output({error: $.create(err)});
    } else {
      // get full updated document.
      $.db.get(response.id).then(function(doc) {
        output({out: $.get('in')});
      });
    }
  });
};
